  `remark` varchar(400) NOT NULL DEFAULT '' COMMENT '订单附言',
  `delivery_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '配送时间',
  `score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '订单送的积分',
