  `divide_store_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '店铺分成',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=187 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

