/*Table structure for table `product` */

DROP TABLE IF EXISTS `product`;

CREATE TABLE `product` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id_1` int(11) NOT NULL DEFAULT '0' COMMENT '平台产品分类1级',
  `category_id_2` int(11) NOT NULL DEFAULT '0' COMMENT '平台产品分类2级',
  `category_id_3` int(11) NOT NULL DEFAULT '0' COMMENT '平台产品分类3级',
  `category_id_4` int(11) NOT NULL DEFAULT '0' COMMENT '平台产品分类4级',
  `category_id_5` int(11) NOT NULL DEFAULT '0' COMMENT '平台产品分类5级',
  `store_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '哪个店的商品',
  `title` varchar(200) NOT NULL COMMENT '产品名称',
  `title_color` varchar(10) NOT NULL DEFAULT '',
  `custom_attribute` varchar(50) NOT NULL DEFAULT '' COMMENT '属性',
  `keyword` varchar(200) NOT NULL DEFAULT '' COMMENT '关键词',
  `abstract` varchar(400) NOT NULL DEFAULT '' COMMENT '摘要',
  `content` text NOT NULL COMMENT '内容',
  `hits` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '浏览数',
  `sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `market_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '市场价格',
  `sell_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '出售价格',
  `weight` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '重量KG',
  `product_num` varchar(50) NOT NULL DEFAULT '' COMMENT '产品货号',
  `stock` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '库存数量',
  `sales` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '出售数量',
  `add_time` int(11) NOT NULL,
  `path` varchar(100) NOT NULL DEFAULT '' COMMENT '封面',
  `brand_name` varchar(50) NOT NULL DEFAULT '' COMMENT '品牌名称',
  `material_name` varchar(50) NOT NULL DEFAULT '' COMMENT '材质',
  `style_name` varchar(50) NOT NULL DEFAULT '' COMMENT '风格',
  `display` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态0=下架；1＝上架；2＝放入库存；',
  `batch_path_ids` varchar(200) NOT NULL DEFAULT '' COMMENT '多图',
  `display_time` int(11) NOT NULL DEFAULT '0' COMMENT '显示时间',
  `remark` varchar(140) NOT NULL DEFAULT '' COMMENT '备注',
  `favorite_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数量',
  `give_score` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '送积分数量',
  `consume_score` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '最大抵扣积分数量',
  `app_content` text COMMENT '手机app产品详细内容',
  `color_size_open` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '规格属性是否开启；0＝关闭；1＝开启',
  `product_color_name` varchar(50) NOT NULL DEFAULT '' COMMENT '规格名称[color]',
  `product_size_name` varchar(50) NOT NULL DEFAULT '' COMMENT '规格名称[size]',
  PRIMARY KEY (`id`),
  KEY `display_index` (`display`),
  KEY `category_id_1_index` (`category_id_1`),
  KEY `category_id_2_index` (`category_id_2`),
  KEY `category_id_3_index` (`category_id_3`),
  KEY `category_id_4_index` (`category_id_4`),
  KEY `category_id_5_index` (`category_id_5`),
  KEY `store_id_index` (`store_id`)
) ENGINE=MyISAM AUTO_INCREMENT=182 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

