/*Table structure for table `score_setting` */

DROP TABLE IF EXISTS `score_setting`;

CREATE TABLE `score_setting` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `login_score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '登录每次送积分',
  `login_score_max` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '一天最多送多少积分',
  `reg_score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '注册送积分',
  `exchange_rmb_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '多少个街贝兑换一元',
  `is_open` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '街贝抵现是否开启0=关闭;1=开启',
  `store_score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '店面推广用户入驻送积分',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

