/*Table structure for table `store` */

DROP TABLE IF EXISTS `store`;

CREATE TABLE `store` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '用户ID',
  `store_name` varchar(100) NOT NULL DEFAULT '' COMMENT '店铺名称',
  `owner_name` varchar(60) NOT NULL DEFAULT '' COMMENT '店主姓名',
  `owner_card` varchar(60) NOT NULL DEFAULT '' COMMENT '店主身份证号',
  `province_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '省ID',
  `city_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '市ID',
  `area_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '地区ID',
  `txt_address` varchar(100) NOT NULL DEFAULT '' COMMENT '省 市 县',
  `address` varchar(255) NOT NULL DEFAULT '' COMMENT '详细地址',
  `mobile` varchar(60) NOT NULL DEFAULT '' COMMENT '手机号',
  `store_category_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '所属店铺分类',
  `store_grade_id` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '店铺等级ID',
  `credit_value` int(11) NOT NULL DEFAULT '0' COMMENT '信誉值',
  `praise_rate` decimal(5,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '好评率',
  `display` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '店铺状态：0＝关闭；1＝开启',
  `close_reason` varchar(255) NOT NULL DEFAULT '' COMMENT '关闭原因',
  `add_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '开店时间',
  `end_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '到期时间',
  `real_name_auth` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '实名认证：0＝未实名认证；1＝已实名认证',
  `store_auth` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '店铺实名认证：0＝未认证；1＝已认证',
  `sort` int(11) unsigned NOT NULL DEFAULT '0',
  `recommended` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '推荐：0＝不推荐；1＝推荐',
  `theme` varchar(60) NOT NULL DEFAULT '' COMMENT '主题模板',
  `store_banner` varchar(100) NOT NULL DEFAULT '' COMMENT 'banner图片',
  `path` varchar(100) NOT NULL DEFAULT '' COMMENT 'LOGO',
  `batch_path_ids` varchar(100) NOT NULL DEFAULT '' COMMENT '店铺展示图片',
  `description` text COMMENT '店铺介绍',
  `im_qq` varchar(60) NOT NULL DEFAULT '' COMMENT 'QQ号',
  `im_ww` varchar(60) NOT NULL DEFAULT '' COMMENT '旺旺号',
  `im_weixin` varchar(60) NOT NULL DEFAULT '' COMMENT '微信号',
  PRIMARY KEY (`id`),
  KEY `user_index` (`user_id`),
  KEY `store_category_id_index` (`store_category_id`),
  KEY `store_grade_id_index` (`store_grade_id`)
) ENGINE=MyISAM AUTO_INCREMENT=80 DEFAULT CHARSET=utf8;

