/*Table structure for table `store_category` */

DROP TABLE IF EXISTS `store_category`;

CREATE TABLE `store_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `store_category_name` varchar(50) NOT NULL COMMENT '店铺分类名称',
  `parent_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '父级ID',
  `sort` int(11) unsigned NOT NULL DEFAULT '0',
  `display` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '0=隐藏；1＝显示',
  `path` varchar(100) NOT NULL DEFAULT '' COMMENT '产品分类图片',
  PRIMARY KEY (`id`),
  KEY `parent_id_index` (`parent_id`),
  KEY `display_index` (`display`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC COMMENT='店铺分类';

