/*Table structure for table `system` */

DROP TABLE IF EXISTS `system`;

CREATE TABLE `system` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `web_site` varchar(400) DEFAULT NULL,
  `index_name` varchar(500) DEFAULT NULL,
  `index_url` varchar(100) DEFAULT NULL,
  `client_index` varchar(100) DEFAULT NULL,
  `site_name` varchar(500) DEFAULT NULL,
  `index_site_name` varchar(200) NOT NULL DEFAULT '',
  `site_copyright` text,
  `site_keycode` varchar(2000) DEFAULT NULL,
  `site_description` varchar(4000) DEFAULT NULL,
  `icp_code` varchar(400) DEFAULT NULL,
  `cache` tinyint(1) DEFAULT '0',
  `cache_time` int(11) DEFAULT '10',
  `html` tinyint(1) DEFAULT '0',
  `html_folder` varchar(50) NOT NULL DEFAULT '',
  `html_level` varchar(1) NOT NULL DEFAULT '0' COMMENT '0=行业级，1=企业级',
  `text_keyword` varchar(50) DEFAULT NULL,
  `link_keyword` varchar(200) DEFAULT NULL,
  `globle_qq_service` text,
  `left_qq_service` text,
  `online_recharge` text,
  `login_score` int(11) DEFAULT '0',
  `register_score` int(11) DEFAULT '0',
  `product_percent` int(11) DEFAULT '0',
  `open_score` tinyint(1) DEFAULT '0',
  `pay_service` text,
  `pay_mode` text,
  `buy_process` text,
  `discount_title` varchar(200) DEFAULT NULL,
  `discount_ad` varchar(200) DEFAULT NULL,
  `discount_add_time` int(11) DEFAULT '0',
  `discount_end_time` int(11) DEFAULT '0',
  `discount_percent` int(11) DEFAULT '100',
  `discount_open` tinyint(1) DEFAULT '0',
  `discount_content` text,
  `exchange_space_min` int(11) DEFAULT '60',
  `exchange_percent` int(11) DEFAULT '100',
  `exchange_open` tinyint(1) DEFAULT '0',
  `reg_agreement` text,
  `score_deductible` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '街贝抵扣金额（单位：街贝/元）',
  `free_coupon_path` varchar(100) DEFAULT '' COMMENT '免单优惠券图片路径',
  `half_price_coupon_path` varchar(100) DEFAULT '' COMMENT '半价优惠券图片路径',
  `half_price_coupon_limit` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '半价优惠券是否限单件 1=限单件，2=支持多件',
  `half_price_coupon_set_money` decimal(5,2) unsigned NOT NULL COMMENT '半价优惠券满多少元',
  `pintuan_start` int(11) unsigned DEFAULT '0' COMMENT '拼团开始时间',
  `pintuan_end` int(11) unsigned DEFAULT '0' COMMENT '拼团结束时间',
  `pintuan_number` int(11) unsigned DEFAULT '0' COMMENT '拼团满多少人',
  `pintuan_minus` tinyint(3) unsigned DEFAULT '0' COMMENT '拼团折扣',
  `pintuan_open` tinyint(1) unsigned DEFAULT '0' COMMENT '是否开启拼团活动 0=关闭 1=开启',
  `free_postage` decimal(5,2) unsigned NOT NULL,
  `parent_presenter_text` text NOT NULL COMMENT '城市合伙人加盟介绍',
  `sub_presenter_text` text NOT NULL COMMENT '店面分销商加盟介绍',
  `presenter_is_open` tinyint(1) NOT NULL DEFAULT '0' COMMENT '推广活动：0＝关闭；1＝开启；',
  `presenter_city_text` text NOT NULL,
  `presenter_store_text` text NOT NULL,
  `android_full_update_version` varchar(10) NOT NULL DEFAULT '',
  `android_full_update_url` varchar(200) NOT NULL DEFAULT '',
  `ios_full_update_version` varchar(10) NOT NULL DEFAULT '',
  `ios_full_update_url` varchar(200) NOT NULL DEFAULT '',
  `wget_version` varchar(10) NOT NULL DEFAULT '' COMMENT '资源包版本号',
  `wget_url` varchar(200) NOT NULL DEFAULT '' COMMENT '资源包下载地址',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

