/*Table structure for table `user` */

DROP TABLE IF EXISTS `user`;

CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_group_id` int(11) NOT NULL,
  `username` varchar(50) NOT NULL COMMENT '登录密码',
  `password` varchar(32) NOT NULL DEFAULT '' COMMENT '支付密码',
  `pay_password` varchar(32) NOT NULL DEFAULT '',
  `nickname` varchar(50) NOT NULL DEFAULT '' COMMENT '昵称',
  `real_name` varchar(50) NOT NULL DEFAULT '' COMMENT '真实姓名',
  `qq_number` varchar(20) NOT NULL DEFAULT '' COMMENT 'QQ号',
  `wangwang_number` varchar(50) NOT NULL DEFAULT '' COMMENT '旺旺号',
  `weixin` varchar(50) NOT NULL DEFAULT '' COMMENT '微信号',
  `email` varchar(100) NOT NULL DEFAULT '' COMMENT '邮箱',
  `add_time` int(11) NOT NULL,
  `display` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态：0＝未激活；1＝已激活；2＝冻结',
  `score` int(11) NOT NULL DEFAULT '0' COMMENT '积分值',
  `experience` int(11) NOT NULL DEFAULT '0' COMMENT '经验值',
  `total` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '账户余额',
  `login_time` int(11) NOT NULL DEFAULT '0' COMMENT '登录时间',
  `ip` varchar(20) NOT NULL DEFAULT '' COMMENT 'IP',
  `ip_address` varchar(50) NOT NULL DEFAULT '' COMMENT 'IP对应的地址',
  `mobile` varchar(20) NOT NULL DEFAULT '' COMMENT '手机号',
  `phone` varchar(20) NOT NULL DEFAULT '',
  `zip` char(6) NOT NULL DEFAULT '',
  `address` varchar(100) NOT NULL DEFAULT '',
  `sex` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0=保密 1=男 2=女',
  `path` varchar(200) DEFAULT '' COMMENT '用户头像',
  `presenter_id` int(11) NOT NULL DEFAULT '0' COMMENT '推荐者用户ID',
  `presenter_username` varchar(50) NOT NULL DEFAULT '' COMMENT '推荐者用户名',
  `remark_time` int(11) NOT NULL DEFAULT '0' COMMENT '设置推荐人时间',
  `remark` varchar(140) NOT NULL DEFAULT '' COMMENT '设置推荐人的备注',
  `pop_code` varchar(32) NOT NULL DEFAULT '' COMMENT '推广码',
  `admin_id` int(11) NOT NULL DEFAULT '0' COMMENT '操作员ID，哪个业务发展的推广员',
  `admin_add_time` int(11) NOT NULL DEFAULT '0' COMMENT '操作时间',
  `wx_openid` varchar(64) NOT NULL DEFAULT '' COMMENT '微信open_id',
  `qq_openid` varchar(64) NOT NULL DEFAULT '' COMMENT 'qq第三方登录openid',
  `wx_unionid` varchar(64) NOT NULL DEFAULT '' COMMENT '微信open_id',
  `province_id` int(11) NOT NULL DEFAULT '0',
  `city_id` int(11) NOT NULL DEFAULT '0',
  `area_id` int(11) NOT NULL DEFAULT '0',
  `txt_address` varchar(100) NOT NULL DEFAULT '',
  `qq_unionid` varchar(64) NOT NULL DEFAULT '' COMMENT 'qq第三方登录openid',
  `wb_uid` varchar(24) DEFAULT '',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=55 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

