/*Table structure for table `user_address` */

DROP TABLE IF EXISTS `user_address`;

CREATE TABLE `user_address` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `buyer_name` varchar(20) NOT NULL COMMENT '姓名',
  `country_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '国',
  `province_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '省',
  `city_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '市',
  `area_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '县',
  `address` varchar(100) NOT NULL DEFAULT '' COMMENT '详细地址',
  `zip` char(6) NOT NULL DEFAULT '' COMMENT '邮编',
  `phone` varchar(20) NOT NULL DEFAULT '' COMMENT '电话',
  `mobile` varchar(20) NOT NULL DEFAULT '' COMMENT '手机',
  `is_default` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '默认值',
  `user_id` int(11) unsigned NOT NULL COMMENT '用户ID',
  `txt_address` varchar(100) NOT NULL DEFAULT '' COMMENT '国 省 市 县',
  PRIMARY KEY (`id`),
  KEY `user_index` (`user_id`),
  KEY `default_index` (`is_default`)
) ENGINE=MyISAM AUTO_INCREMENT=123 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

