  `wx_unionid` varchar(64) NOT NULL DEFAULT '' COMMENT '微信open_id',
  `province_id` int(11) NOT NULL DEFAULT '0',
  `city_id` int(11) NOT NULL DEFAULT '0',
  `area_id` int(11) NOT NULL DEFAULT '0',
  `txt_address` varchar(100) NOT NULL DEFAULT '',
  `qq_unionid` varchar(64) NOT NULL DEFAULT '' COMMENT 'qq第三方登录openid',
  `wb_uid` varchar(24) DEFAULT '',
  `push_cid` varchar(100) NOT NULL DEFAULT '' COMMENT '个推用户cid',
  `is_check_email` tinyint(1) NOT NULL DEFAULT '0' COMMENT '邮箱是否验证1＝已验证；0＝未验证',
  `is_check_mobile` tinyint(1) NOT NULL DEFAULT '0' COMMENT '验证手机；1＝已验证；0＝未验证',
  `id_card` varchar(18) NOT NULL DEFAULT '',
  `is_id_card_auth` tinyint(1) NOT NULL COMMENT '实名认证',
  `evaluate_a` int(11) NOT NULL DEFAULT '100' COMMENT '好评数',
  `evaluate_b` int(11) NOT NULL DEFAULT '0' COMMENT '中评数',
  `evaluate_c` int(11) NOT NULL DEFAULT '0' COMMENT '差评数',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=62 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

